import React, { Component } from 'react';
import Card from './Card';
import Axios from 'axios';

export default class Deck extends Component {
    constructor() {
        super();
        this.state = {
            cards: []
        }
    }

    componentDidMount() {
        Axios.get('http://localhost:8080/get/' + this.props.cardNo)
            .then(res => {
                const CardData = res.data.value;
                console.log(CardData);
                this.setState({ cards: CardData });
                console.log(this.state);
            }).catch(error => {
                console.log(error);
            })
    }

    render() {
        if (this.state.cards.length === 0) {
            return false;
        } else {
            return (
                <div>
                    <Card cardInfo={this.state.cards} />
                </div>

            )
        }
    }
}
